// src/pages/Home.tsx
import React, { useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { fetchCenters } from '../store/centersSlice';
import { RootState, AppDispatch } from '../store';
import CenterCard from '../components/CenterCard';
import { Grid, CircularProgress, Typography } from '@mui/material';

const Home: React.FC = () => {
  const dispatch: AppDispatch = useDispatch();
  const centers = useSelector((state: RootState) => state.centers.centers);
  const centerStatus = useSelector((state: RootState) => state.centers.status);
  const error = useSelector((state: RootState) => state.centers.error);

  useEffect(() => {
    if (centerStatus === 'idle') {
      dispatch(fetchCenters());
    }
  }, [centerStatus, dispatch]);

  let content;

  if (centerStatus === 'loading') {
    content = <CircularProgress />;
  } else if (centerStatus === 'succeeded') {
    content = (
      <Grid container spacing={3}>
        {centers.map((center) => (
          <Grid item key={center.id} xs={12} sm={6} md={4}>
            <CenterCard center={center} />
          </Grid>
        ))}
      </Grid>
    );
  } else if (centerStatus === 'failed') {
    content = <Typography color="error">{error}</Typography>;
  }

  return <div>{content}</div>;
};

export default Home;
